mapper.select:
SELECT id, name, age
FROM student
WHERE id = #{id}

mapper.update:
UPDATE student
SET name = #{name}
WHERE id = #{id}

mapper.select:
SELECT ST.ID,
ST.NAME,
ST.SEX,
ST.BIRTHDAY,
ST.PHOTO,
ST.CLASS_ID,
ST.PLACE_ID
FROM student ST


        ST.NAME LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')


        AND ST.SEX = #{sex, jdbcType=INTEGER}


        AND ST.BIRTHDAY = #{birthday, jdbcType=DATE}


        AND ST.CLASS_ID = #{classId, jdbcType=VARCHAR}


        AND ST.CLASS_ID = #{classEntity.classId, jdbcType=VARCHAR}


        AND ST.PLACE_ID = #{placeId, jdbcType=VARCHAR}


        AND ST.PLACE_ID = #{placeEntity.placeId, jdbcType=VARCHAR}


        AND ST.ID = #{Id, jdbcType=VARCHAR}

mapper.update:
UPDATE student


        NAME = #{name},


        SEX = #{sex},


        BIRTHDAY = #{birthday},


        PHOTO = #{photo, javaType=byte[], jdbcType=BLOB, typeHandler=org.apache.ibatis.type.BlobTypeHandler},


        CLASS_ID = #{classId}


        PLACE_ID = #{placeId}


WHERE ID = #{id};

